				$data['link_pdfinv_packing'] = $this->url->link('sale/order/pdf_packingslip', 'token=' . $this->session->data['token'] . '&order_id=' . (int)$this->request->get['order_id'], 'SSL');
				$data['link_pdfinv_backup'] = $this->url->link('sale/order/pdf_backup', 'token=' . $this->session->data['token'] . '&order_id=' . (int)$this->request->get['order_id'], 'SSL');
				$data['pdf_invoice_manual_backup'] = $this->config->get('pdf_invoice_backup_moment') == 'manual';

				// invoice number editor
				$data['pdf_invoice_manual_inv_no'] = $this->config->get('pdf_invoice_manual_inv_no');
