				$data['text_pdf_edit'] = $this->language->get('text_pdf_edit');
				$data['text_pdf_now'] = $this->language->get('text_pdf_now');
			} else {
				$this->data['pdfinv_ocv2'] = false;
				$this->data['pdfinv_ocv21'] = false;

