				$this->data['link_pdfinv_packing'] = $this->url->link('sale/order/pdf_packingslip', 'token=' . $this->session->data['token'] . '&order_id=' . (int)$this->request->get['order_id'], 'SSL');
				$this->data['link_pdfinv_backup'] = $this->url->link('sale/order/pdf_backup', 'token=' . $this->session->data['token'] . '&order_id=' . (int)$this->request->get['order_id'], 'SSL');
